export const styles={
    subFont:{
        fontSize: 16,
        fontFamily: "'Open Sans', sans-serif",
        fontWeight: 400,
        letterHeight: 22,
        opacity: "0.7",
        color: "#707070",
        marginTop: "2px"
    },
    card:{
        boxShadow: "12px 12px 16px #d6d6d6",
        maxWidth: 560,
        borderRadius: "6px",
        marginBottom: "16%"
    },
    boldFont:{
        fontSize: 26,
        fontFamily: "'Open Sans', sans-seri",
        fontWeight: 600,
        letterHeight: 36
    },
    boldFont2:{
        fontSize: 16,
        fontFamily: "'Open Sans', sans-seri",
        fontWeight: 600,
        letterHeight: 22
    },
    topFont:{
        fontSize: 16,
        fontFamily: "'Roboto', sans-serif",
        fontWeight: 400,
        letterHeight: 19,
        opacity: "0.7",
        color: "#707070",
        marginTop: "8px",
        marginBottom: "8px"
    },
    regFont:{
        fontSize: 16,
        fontFamily: "'Open Sans', sans-serif",
        fontWeight: 400,
        opacity: "0.5"
    },
    avatar:{
        width: 60,
        height: 60,
        margin: 20
    }
}
