import React, { Component } from 'react';
import { Grid } from '@material-ui/core';
import BlogCard from '../../../Blog/components/BlogCard/BlogCard';
import Responses from '../Responses/Responses';
import axios from 'axios';

class LowerSection extends Component {
    state = { 
        data:[]
     }
    styles={
        heading:{
            fontSize:24,
            padding: "6px 0px 14px 0px",
            fontFamily: "'Open Sans', sans-seri",
            fontWeight: 600,
            lineHeight: "33px",
            marginTop: "8%",
            textAlign: "center"
        }
    }
    getRelatedData=()=>{
        // axios.get(`https://jsonplaceholder.typicode.com/posts?userId=${p||this.state.page}`)
        axios.get(`http://localhost:8000/api/posts/`)
        .then((response)=>{
            // console.log("Response: "+response.data)
            this.setState({
                data: response.data.results.slice(0,4),
                page: 1
            })
        })
        .catch(e=>console.log(e))
    }
    componentDidMount=()=>{
        this.getRelatedData()
    }
    render() { 
        console.log(this.props)
        const renderCard=this.state.data.map((data)=>{
            return(
            <BlogCard
                cover={data.cover}
                heading={data.technologies}
                subHeading={data.title}
                pic={data.author.profile_pic}
                content={data.content}
                author={data.author.username}
                date={data.publish}
                detailLink={data.detail}
            />
                )
        }
        )
        return ( 
            <React.Fragment>
                <Grid
                container
                direction="row"
                justify="center"
                style={{marginBottom: "10%"}}
                >
                    <Grid item xs={12}>
                    <div style={this.styles.heading} >You might be interested in</div>
                    </Grid>
                    <Grid 
                    container 
                    direction="row" 
                    justify="center" 
                    spacing={24} 
                    style={{width: "100%",margin: 0}}
                    >
                       {renderCard}
                        <Grid item xs={12}>
                            <Responses res={this.props.res} />
                        </Grid>
                    </Grid>
                </Grid>
            </React.Fragment>
         );
    }
}
 
export default LowerSection;
